.print-format {
color: #666666;
/* font-size: 14px; */
}
.print-format .data-field {
margin: 0 -15px;
border-left: 10px solid #ffffff; */
}
.print-format label { color: #5c87c7; }

.print-format .print-heading {
border-color: #e7e7e8;
/* padding: 0; */
/* margin: 0; */
}
.print-format .print-heading h2 { color: #5c87c7; font-weight: bold; text-transform: uppercase; }
.print-format .print-heading h2 small { color: #a5b6de; }


.print-format .important,
.print-format [data-fieldname="due_date"],
.print-format [data-fieldname="company"] {
background-color: #dbebf6;
}
.print-format [data-fieldname="grand_total"],
.print-format [data-fieldname="posting_date"] {
background-color: #e7e7e8;
}

.print-format .important div:first-child,
.print-format [data-fieldname="due_date"] div:first-child,
.print-format [data-fieldname="company"] div:first-child {
border-left: 7px solid #5c87c7;
padding-left: 8px;
}


.print-format [data-fieldname="due_date"] .value,
.print-format [data-fieldname="company"] .value {
font-size: 120%;
font-weight: bold;
}
.print-format [data-fieldname="due_date"] .value {
color: #5c87c7;
}


.print-format [data-fieldname="terms"] {
background-color: #f5f5f5;
border-radius: 5px;
padding: 10px 15px;
margin: 5px 0;
}

.print-format [data-fieldname="bank"] div:first-child,
.print-format [data-fieldname="bank_account_no"] div:first-child,
.print-format [data-fieldname="branch_code"] div:first-child,
.print-format [data-fieldname="swift_number"] div:first-child {
border-left: 7px solid #5c87c7;
padding-left: 8px;
}
.print-format [data-fieldname="bank"],
.print-format [data-fieldname="bank_account_no"],
.print-format [data-fieldname="branch_code"],
.print-format [data-fieldname="swift_number"] {
background-color: #f5f5f5;
}


.print-format .table-bordered > thead > tr > th,
.print-format .table-bordered > tbody > tr > th,
.print-format .table-bordered > tfoot > tr > th,
.print-format .table-bordered > thead > tr > td,
.print-format .table-bordered > tbody > tr > td,
.print-format .table-bordered > tfoot > tr > td {
border: 2px solid #ffffff;
}
.print-format .table-bordered > tbody > tr > td:nth-child(1),
.print-format .table-bordered > tbody > tr > td:nth-child(2) {
font-size: 120%;
font-weight: bold;
}
.print-format th { background-color: #5c87c7 !important; color: white !important; }
.print-format td { background-color: #dbebf6 !important;  }

