/* Base */
body {
    color: #ddd;
    background-color: #333;
}

a {
    color: #FF5722;
    /* color: #2196f3; */
}
a:hover, a:focus {
    color: #FF9800;
    /* color: #81d4fa; */
}

/* Main */
#page-desktop {
    background-color: #3F51B5;
}

.modal-backdrop {
    background-color: #1d2526 !important;
}

.page-head {
    border-color: #111;
    background-color: #2c2c2c;
}
.page-form {
    border-color: #111;
    background-color: #2c2c2c;
}

.layout-main-section {
    border-color: #111;
}

.module-body {
    border-color: #111;
}
.module-section {
    border-color: #111;
}
.module-section:nth-child(even) {
    background-color: #2c2c2c;
}

.form-section:not(:last-child), .form-inner-toolbar {
    border-color: #111;
}

/* Navbars */
body[data-route=""] .navbar-default, body[data-route="desktop"] .navbar-default {
    background-color: rgba(34, 34, 34, .902);
    border-color: rgba(17, 17, 17, .102);
}

.navbar-default {
    background-color: #333;
    border-color: #111;
}

.navbar-default .navbar-nav > li > a:hover, .navbar-default .navbar-nav > li > a:focus {
    color: #ddd;
}
.navbar-default .navbar-nav > .open > a, .navbar-default .navbar-nav > .open > a:hover, .navbar-default .navbar-nav > .open > a:focus {
    color: #fcfcfc;
    background-color: #444;
}

/* Drop downs */
.dropdown-menu {
    background-color: #2C2C2C;
    border-color: rgba(0, 0, 0, .35);
    box-shadow: 0 6px 12px rgba(0, 0, 0, .375);
}
.dropdown-menu > li > a {
    color: #CCCCCC;
}
.dropdown-menu > li > a:hover, .dropdown-menu > li > a:focus {
    color: #FCFCFC;
    background-color: #444444;
}
.dropdown-menu .divider {
    background-color: #000000;
}

/* Side menu */
.nav > li > a:hover, .nav > li > a:focus {
    background-color: #444;
}
.nav-pills > li.active > a, .nav-pills > li.active > a:hover, .nav-pills > li.active > a:focus {
    background-color: #3c3c3c;
}

.layout-side-section .overlay-sidebar {
    background-color: #333;
}

/* Forms */
.form-control {
    color: #dddddd;
    background-color: #11111188;
    border-color: #555;
}
.form-control.bold {
    color: #fcfcfc;
    background-color: #222;
}

.ql-bubble .ql-editor {
    border-color: #111;
}

#navbar-search {
    background-color: #11111188;
}
.awesomplete > ul {
    background-color: #2C2C2C;
    border-color: #555;
}
.awesomplete > ul > li:hover, .awesomplete > ul > li[aria-selected=true] {
    background-color: #444;
    color: #FCFCFC;
}

/* Buttons */
.btn-default {
    background-color: #3c3c3c;
}
.btn-default:hover, .btn-default:focus, .btn-default.focus, .btn-default:active, .btn-default.active, .open > .dropdown-toggle.btn-default {
    background-color: #555;
    border-color: rgba(0, 0, 0, 0);
}

/* Modals */
.modal-content {
    background-color: #2c2c2c;
}

/* Chat */
.panel {
    background-color: #333;
}
.panel-default {
    border-color: #111;
}
.panel-default > .panel-heading {
    color: #ddd;
    background-color: #2c2c2c;
    border-color: #111;
}

.frappe-chat > .frappe-chat-popper > .frappe-chat-popper-collapse > .panel.panel-bg {
    background-image: none;
}
.chat-room-footer .chat-form {
    border-color: #111;
}
.chat-room-footer .chat-form .input-group-btn .btn {
    background-color: #222;
}
.chat-list .chat-list-item .chat-bubble .chat-bubble-content {
    color: #111;
}

/* Timeline */
.timeline-head {
    background-color: #2c2c2c;
    border-color: #111;
}
.timeline-head .comment-input-header {
    background-color: #222;
    border-color: #111;
}

/* Tables */
.filter-list .tag-filters-area {
    border-color: #111;
}
.list-row-container {
    border-color: #111;
}
.list-row-head {
    background-color: #2c2c2c;
    border-color: #111 !important;
}
.list-row:hover {
    background-color: #444;
}
