/* Base */
body {
    color: #ddd;
    background-color: #333 !important;
}

code {
    background-color: #150a0ddd;
}
pre {
    color: #ddd;
    background-color: #150a0ddd;
    border-color: rgba(0, 0, 0, .35);
}

#freeze {
    background-color: #36414C;
}

/* Navbars */
.navbar-default .navbar-toggle {
    border-color: rgba(0, 0, 0, .35);
}
.navbar-default .navbar-toggle:hover, .navbar-default .navbar-toggle:focus {
    background-color: #555;
}
.navbar-toggle {
    margin-top: 0;
    margin-bottom: 0;
}

/* Drop downs */
.dropdown-menu {
    background-color: #2C2C2C;
    border-color: rgba(0, 0, 0, .35);
    box-shadow: 0 6px 12px rgba(0, 0, 0, .375);
}
.dropdown-menu > li > a {
    color: #CCCCCC;
}
.dropdown-menu > li > a:hover, .dropdown-menu > li > a:focus {
    color: #FCFCFC;
    background-color: #444444;
}

/* Side menu */
.nav > li > a:hover, .nav > li > a:focus {
    background-color: #444;
}
.nav-pills > li.active > a, .nav-pills > li.active > a:hover, .nav-pills > li.active > a:focus {
    background-color: #3c3c3c;
}

.layout-side-section .overlay-sidebar {
    background-color: #333;
}

.web-sidebar .sidebar-item a.active {
    color: #fcfcfc;
}

/* Modals */
.modal-content {
    background-color: #2c2c2c;
}

/* Page */
body .hero-and-content {
    background-color: rgba(51, 51, 51, .902);
}
body .page-card {
    background-color: #2c2c2c;
    border-color: #111;
}
body .page-card .page-card-head {
    border-color: #111;
}
body .page-card .page-card-head .indicator  {
    color: #ddd;
}

/* Forms */
.form-control {
    color: #dddddd;
    background-color: #11111188;
    border-color: #555;
}
.form-control.bold {
    color: #fcfcfc;
    background-color: #222;
}

/* Buttons */
.btn-default {
    background-color: #3c3c3c;
}
.btn-default:hover, .btn-default:focus, .btn-default.focus, .btn-default:active, .btn-default.active, .open > .dropdown-toggle.btn-default {
    background-color: #555;
    border-color: rgba(0, 0, 0, 0);
}

/* Chat */
.panel {
    background-color: #333;
}
.panel-default {
    border-color: #111;
}
.panel-default > .panel-heading {
    color: #ddd;
    background-color: #2c2c2c;
    border-color: #111;
}

.frappe-chat > .frappe-chat-popper > .frappe-chat-popper-collapse > .panel.panel-bg {
    background-image: none;
}
.chat-room-footer .chat-form {
    border-color: #111;
}
.chat-room-footer .chat-form .input-group-btn .btn {
    background: #222;
}
.chat-list .chat-list-item .chat-bubble .chat-bubble-content {
    color: #111;
}

/* Footer */
.footer-subscribe .btn-default {
    border-color: #555;
}

/* Others */
.product-image.missing-image {
    background-color: #1c1c1c;
}

